import React, { useState } from 'react';

function GiftSection() {
  const giftCards = [
    { id: 1, amount: 25, description: 'Perfect for a small treat', image: 'data:image/jpeg;base64,' },
    { id: 2, amount: 50, description: 'Great for a special occasion', image: 'data:image/jpeg;base64,' },
    { id: 3, amount: 100, description: 'Ideal for a big surprise', image: 'data:image/jpeg;base64,' },
    { id: 4, amount: 150, description: 'For that extra special gift', image: 'data:image/jpeg;base64,' },
    { id: 5, amount: 200, description: 'Go all out with this one', image: 'data:image/jpeg;base64,' },
    { id: 6, amount: 250, description: 'The ultimate gift experience', image: 'data:image/jpeg;base64,' },
  ];

  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    amount: '',
    message: '',
  });
  const [errors, setErrors] = useState({});
  const [submitting, setSubmitting] = useState(false);
  const [success, setSuccess] = useState(false);

  const validate = () => {
    const newErrors = {};
    if (!formData.fullName.trim()) newErrors.fullName = 'Full name is required';
    if (!formData.email.trim()) newErrors.email = 'Email is required';
    else if (!/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/.test(formData.email))
      newErrors.email = 'Invalid email address';
    if (!formData.phoneNumber.trim()) newErrors.phoneNumber = 'Phone number is required';
    if (!formData.amount) newErrors.amount = 'Please select a gift card amount';
    return newErrors;
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    setErrors((prev) => ({ ...prev, [name]: undefined }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccess(false);
    const validationErrors = validate();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    setSubmitting(true);
    setTimeout(() => {
      setSubmitting(false);
      setSuccess(true);
      setFormData({
        fullName: '',
        email: '',
        phoneNumber: '',
        amount: '',
        message: '',
      });
    }, 1200);
  };

  return (
    <div className="w-full bg-white px-4 py-8 md:px-6 md:py-[8.75rem]">
      <div className="mx-auto max-w-[1400px]">
        {/* Heading */}
        <div className="max-w-[43.75rem] mx-auto text-center mb-10">
          <h2 className="text-[1.75rem] md:text-[2.5rem] lg:text-[3.5rem] font-medium text-[#0D0D0D] font-['Inter'] tracking-[-0.075rem] md:tracking-[-0.125rem] lg:tracking-[-0.175rem] leading-[2.1875rem] md:leading-[3.125rem] lg:leading-[3.875rem]">
            Give the Perfect Gift for Any Occasion
          </h2>
        </div>

        {/* Gift Cards Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 mb-16">
          {giftCards.map((card) => (
            <div
              key={card.id}
              className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300 flex flex-col"
            >
              <img
                src={card.image}
                alt={`$${card.amount} Gift Card`}
                className="w-full h-48 object-cover"
              />
              <div className="p-4 flex flex-col flex-1">
                <h3 className="text-lg font-semibold text-[#0D0D0D] font-['Inter']">
                  ${card.amount} Gift Card
                </h3>
                <p className="text-sm text-gray-600 mt-1 flex-1">{card.description}</p>
                <button className="mt-4 w-full bg-[#f45c24] text-white py-2 rounded-md hover:bg-[#d6501f] transition-colors duration-200">
                  Buy Now
                </button>
              </div>
            </div>
          ))}
        </div>

        {/* Gift Card Purchase Form */}
        <div className="max-w-xl mx-auto bg-white rounded-lg shadow-md p-8">
          <div className="flex flex-col gap-1 mb-4">
            <h3 className="text-2xl md:text-3xl font-bold text-[#0D0D0D] font-['Inter'] mb-1">
              Purchase a Gift Card
            </h3>
            <p className="text-base text-gray-600 mb-2">Fill out the form below to send a gift card.</p>
            {success && (
              <span className="text-green-600 text-[1rem] mt-2">
                Thank you! Your gift card request has been submitted.
              </span>
            )}
          </div>
          <form className="flex flex-col gap-4" onSubmit={handleSubmit} noValidate>
            <div>
              <label className="block text-sm font-medium text-gray-700" htmlFor="fullName">
                Full Name <span className="text-red-500">*</span>
              </label>
              <input
                id="fullName"
                name="fullName"
                type="text"
                className={`mt-1 block w-full rounded-md border px-3 py-2 font-['Inter'] ${errors.fullName ? 'border-red-500' : 'border-gray-300'} focus:border-[#f45c24] focus:ring-1 focus:ring-[#f45c24]`}
                value={formData.fullName}
                onChange={handleInputChange}
                required
              />
              {errors.fullName && (
                <span className="text-xs text-red-600">{errors.fullName}</span>
              )}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700" htmlFor="email">
                Email <span className="text-red-500">*</span>
              </label>
              <input
                id="email"
                name="email"
                type="email"
                className={`mt-1 block w-full rounded-md border px-3 py-2 font-['Inter'] ${errors.email ? 'border-red-500' : 'border-gray-300'} focus:border-[#f45c24] focus:ring-1 focus:ring-[#f45c24]`}
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              {errors.email && (
                <span className="text-xs text-red-600">{errors.email}</span>
              )}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700" htmlFor="phoneNumber">
                Phone Number <span className="text-red-500">*</span>
              </label>
              <input
                id="phoneNumber"
                name="phoneNumber"
                type="tel"
                className={`mt-1 block w-full rounded-md border px-3 py-2 font-['Inter'] ${errors.phoneNumber ? 'border-red-500' : 'border-gray-300'} focus:border-[#f45c24] focus:ring-1 focus:ring-[#f45c24]`}
                value={formData.phoneNumber}
                onChange={handleInputChange}
                required
              />
              {errors.phoneNumber && (
                <span className="text-xs text-red-600">{errors.phoneNumber}</span>
              )}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700" htmlFor="amount">
                Gift Card Amount <span className="text-red-500">*</span>
              </label>
              <select
                id="amount"
                name="amount"
                className={`mt-1 block w-full rounded-md border px-3 py-2 font-['Inter'] ${errors.amount ? 'border-red-500' : 'border-gray-300'} focus:border-[#f45c24] focus:ring-1 focus:ring-[#f45c24]`}
                value={formData.amount}
                onChange={handleInputChange}
                required
              >
                <option value="">Select amount</option>
                {giftCards.map((card) => (
                  <option key={card.id} value={card.amount}>
                    ${card.amount}
                  </option>
                ))}
              </select>
              {errors.amount && (
                <span className="text-xs text-red-600">{errors.amount}</span>
              )}
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700" htmlFor="message">
                Message (optional)
              </label>
              <textarea
                id="message"
                name="message"
                className="mt-1 block w-full rounded-md border border-gray-300 px-3 py-2 font-['Inter'] focus:border-[#f45c24] focus:ring-1 focus:ring-[#f45c24]"
                rows={3}
                value={formData.message}
                onChange={handleInputChange}
              />
            </div>
            <button
              type="submit"
              className="mt-2 bg-[#f45c24] text-white py-2 px-4 rounded-md hover:bg-[#d6501f] transition-colors duration-200 font-semibold font-['Inter'] disabled:opacity-60"
              disabled={submitting}
            >
              {submitting ? 'Sending...' : 'Send Gift Card'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default GiftSection;
